import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom'
import { getOnePost } from '../services/posts'
import {getAllComments, postComment} from '../services/comments'

export default function PostDetail(props) {
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const { posts, handleDelete } = props;
  const { id } = useParams();

  useEffect(() => {
    if (posts.length) {
      const onePost = posts.find((post) => post.id === Number(id));
      setPost(onePost)
    }
  }, [posts, id])

  useEffect(() => {
    const fetchComments = async () => {
      const commentData = await getAllComments(id);
      setComments(commentData);
    }
    fetchComments();
  }, [id])

  const handleCreateComment = async (commentData) => {
    const newComment = await postComment(commentData);
    setComments(prevState => [...prevState, newComment]);
  }


  return (

    <div className="post-details">
      <h1>Activity</h1>
        {
          post &&
          <div className="card">
            <h3>{post.title}</h3>
            <p>{post.activity_type}</p>
            <p>{post.distance}</p>
            <p>{post.elevation_gain}</p>
            <p>{post.duration}</p>
          <img src={post.activity_photo} alt={post.title} />
          <div>
            {comments.map((comment, index) => (
              <div key={index}>
                <div>{comment.content}</div>
                <div>{comment.user.username}</div>    
          </div>
            ))}
          </div>
          <br></br>
            <Link to='/posts'>Back</Link>
            <Link to={`/posts/${post.id}/edit`}><button>Edit</button></Link>
            <button onClick={() => handleDelete(post.id)}>Delete</button>
          </div>
        }
      </div>

  )
}
